  v_texcoord = a_texcoord;
  v_planecoord = a_planecoord;
  v_position = a_position.xy;
}

A.frag:
#extension GL_OES_standard_derivatives : enable
precision highp float;
uniform sampler2D u_fontTexture;
uniform vec4 u_crop;
uniform float u_0;
uniform float u_1;
uniform float u_2;
uniform float u_time;
uniform vec2 u_position;

varying vec2 v_texcoord;
varying vec2 v_planecoord;
varying vec2 v_position;

const float TWO_PI = 6.28318530718;
const float vertices = 10.;
const float startIndex = vertices;
const float endIndex = vertices * 2.;

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float median(float r, float g, float b) {
    return max(min(r, g), min(max(r, g), b));
}

float charMask(vec2 osc) {
  // MSDF -> SDF
  vec3 sample = texture2D(u_fontTexture, v_texcoord +vec2(floor(osc.x*1000000. + noise(u_time*10.)*100000.))).rgb;
  float sigDist = median(sample.r, sample.g, sample.b) - 0.5;
  sigDist = clamp(sigDist+0.5, 0., 1.0);
  // Crop
  if (u_crop.x > v_texcoord.x 
  ||  u_crop.y < v_texcoord.x
  ||  u_crop.z > v_texcoord.y 
  ||  u_crop.w < v_texcoord.y) {
    sigDist = 0.;
  }

  return step(0.5,sigDist);
}

void main() {

  vec2 osc = vec2(u_0-u_position.x);

  float dist = length(u_position-vec2(u_0, u_1));
  float time = u_time * 5. + u_position.x*100.;
  vec2 uv = v_planecoord;
  uv -= .5;

  gl_FragColor = vec4(vec3(0.),charMask(osc));
}

